import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function Create() {
  const [form, setForm] = useState({
    agent_name: "",
    transaction_number: "",
    sale: "",
    date: ""
  });
  const navigate = useNavigate();

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };

    await fetch("http://localhost:5000/transaction/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ 
    agent_name: "",
    transaction_number: "",
    sale: "",
    date: "" });
    navigate("/transaction");
  }

  // This following section will display the form that takes the input from the user.
  return (
    <div>
      <h3>Create New Transaction</h3>
      <form onSubmit={onSubmit}>
        <div className="form-group">
         <label htmlFor="agent_name">Agent Name</label>
          <select
            className="form-control"
            id="agent_name"
            value={form.agent_name}
            onChange={(e) => updateForm({ agent_name: e.target.value })}
          >
            <option value="">Select Agent</option>
            <option value="Goku">Goku</option>
            <option value="Lelouch">Lelouch</option>
            <option value="Naruto">Naruto</option>
            <option value="Bleach">Bleach</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="transaction_number">Transaction Number</label>
          <input
            type="text"
            className="form-control"
            id="transaction_number"
            value={form.transaction_number}
            onChange={(e) => updateForm({ transaction_number: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label htmlFor="sale">Sale</label>
          <input
            type="text"
            className="form-control"
            id="sale"
            value={form.Sale}
            onChange={(e) => updateForm({ Sale: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label htmlFor="date">Date</label>
          <input
            type="text"
            className="form-control"
            id="date"
            value={form.date}
            onChange={(e) => updateForm({ date: e.target.value })}
          />
        </div>
        
        <div className="form-group">
          <input
            type="submit"
            value="Create Transaction"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
  );
}
