import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";

// Modal component for confirming deletion
const DeleteModal = (props) => {
  return (
    <div className="modal" tabIndex="-1" role="dialog">
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Confirm Deletion</h5>
            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <p>Are you sure you want to delete this record?</p>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" data-dismiss="modal">No/Back</button>
            <button type="button" className="btn btn-primary" onClick={props.onDelete}>Yes/Confirm</button>
          </div>
        </div>
      </div>
    </div>
  );
}

const Record = (props) => {
  const [showModal, setShowModal] = useState(false);

  // Handler for showing the modal
  const handleShowModal = () => {
    setShowModal(true);
  }

  // Handler for hiding the modal
  const handleHideModal = () => {
    setShowModal(false);
  }

  return (
    <tr>
      <td>{props.record.First_Name}</td>
      <td>{props.record.Last_Name}</td>
      <td>{props.record.Email}</td>
      <td>{props.record.Region}</td>
      <td>{props.record.Rating}</td>
      <td>{props.record.Fee}</td>
      <td>{props.record.Sale}</td>
      <td>
        <Link className="btn btn-link" to={`/edit/${props.record._id}`}>Edit</Link> |
        <button className="btn btn-link" onClick={handleShowModal}>Delete</button>
        {showModal && <DeleteModal onDelete={() => props.deleteRecord(props.record._id)} onHide={handleHideModal} />}
      </td>
    </tr>
  );
};

export default function RecordList() {
  const [records, setRecords] = useState([]);

  // This method fetches the records from the database.
  useEffect(() => {
    async function getRecords() {
      const response = await fetch(`http://localhost:5000/record/`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const records = await response.json();
      setRecords(records);
    }

    getRecords();

    return;
  }, [records.length]);

  // This method will delete a record
  async function deleteRecord(id) {
    await fetch(`http://localhost:5000/record/${id}`, {
      method: "DELETE"
    });

    const newRecords = records.filter((el) => el._id !== id);
    setRecords(newRecords);
  }

  // This method will map out the records on the table
  function recordList() {
    return records.map((record) => {
      return (
        <Record
          record={record}
          deleteRecord={() => deleteRecord(record._id)}
          key={record._id}
        />
      );
    });
  }

   // This following section will display the table with the records of individuals.
   return (
    <div>
      <div>
        <NavLink className="btn btn-secondary mt-2" to="/create">
          Create Agent
        </NavLink>
      </div>
      <h3 style={{ textAlign: "center" }}>Record List</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Region</th>
            <th>Rating</th>
            <th>Fee</th>
            <th>Sale</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{recordList()}</tbody>
      </table>
    </div>
  );
}
