// RecordList.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

// Record component displays a single record in a table row
const Record = ({ record }) => {
  const [showConfirmationModal, setShowConfirmationModal] = useState(false);

  // ConfirmationModal component displays a modal dialog box for confirming deletion
  const ConfirmationModal = ({ message, onConfirm, onCancel }) => {
    return (
      <>
        {showConfirmationModal && (
          <div className="modal">
            <div className="modal-content">
              <p>{message}</p>
              <div>
                <button onClick={onConfirm}>Confirm</button>
                <button onClick={onCancel}>Cancel</button>
              </div>
            </div>
          </div>
        )}
      </>
    );
  };

  // This method deletes a record
  const deleteRecord = async (id) => {
    await fetch(`http://localhost:5000/record/${id}`, {
      method: "DELETE"
    });
  };

  // This method handles the deletion of a record
  const handleDeleteRecord = async () => {
    await deleteRecord(record._id);
    setShowConfirmationModal(false);
  };

  // This method handles the cancelation of a record deletion
  const handleCancelDelete = () => {
    setShowConfirmationModal(false);
  };

  // Render a row in the table with the record data and edit/delete buttons
  return (
    <tr>
      <td>{record.First_Name}</td>
      <td>{record.Last_Name}</td>
      <td>{record.Email}</td>
      <td>{record.Region}</td>
      <td>{record.Rating}</td>
      <td>{record.Fee}</td>
      <td>{record.Sale}</td>
      <td>
        <Link className="btn btn-link" to={`/edit/${record._id}`}>
          Edit
        </Link>{" "}
        |
        <button
          className="btn btn-link"
          onClick={() => setShowConfirmationModal(true)}
        >
          Delete
        </button>
        <ConfirmationModal
          message="Are you sure you want to delete this record?"
          showConfirmationModal={showConfirmationModal}
          onConfirm={handleDeleteRecord}
          onCancel={handleCancelDelete}
        />
      </td>
    </tr>
  );
};

// RecordList component displays a table of records
const RecordList = () => {
  const [records, setRecords] = useState([]);

  // This method fetches the records from the database.
  useEffect(() => {
    async function getRecords() {
      const response = await fetch(`http://localhost:5000/record/`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const data = await response.json();
      setRecords(data); // <--- set the state with the fetched data
    }

    getRecords();
  }, []);

  return (
    <table>
      <thead>
        <tr>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Email</th>
          <th>Region</th>
          <th>Rating</th>
          <th>Fee</th>
          <th>Sale</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {records.map((record) => (
          <Record key={record._id} record={record} />
        ))}
      </tbody>
    </table>
  );
};
