import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { useNavigate } from "react-router-dom";
import { setToken } from "./auth";
import { NavLink } from "react-router-dom";
import { Alert } from 'react-bootstrap';
import Navbar from "./navbar";

function Login() {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [showAlert, setShowAlert] = useState(false);
  const [alertVariant, setAlertVariant] = useState("");
  const [alertMessage, setAlertMessage] = useState("");
  const navigate = useNavigate();
  const isLoggedIn = false; // Set isLoggedIn to false on the Login page
  
  const uuid = require('uuid');
  const cookieParser = require('cookie-parser');
  
  app.use(cookieParser());
  
  app.post('/login', async (req, res) => {
    try {
      // Validate user credentials
  
      // Create session token using UUID
      const sessionToken = uuid.v4();
  
      // Set expiration date to 24 hours from now
      const expirationDate = new Date();
      expirationDate.setTime(expirationDate.getTime() + (24 * 60 * 60 * 1000));
  
      // Save session to MongoDB
      const session = {
        session_token: sessionToken,
        user_id: userId, // Get user ID from database query
        expiration_date: expirationDate
      };
      await db.collection('sessions').insertOne(session);
  
      // Set session token cookie
      res.cookie('session_token', sessionToken, { expires: expirationDate });
  
      // Redirect to home page
      res.redirect('/home');
    } catch (err) {
      console.error(err);
      res.status(500).send('Internal server error');
    }
  });
  

  const loginSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (response.ok) {
        setToken(data.token);
        navigate("/Home_page");
      } else {
        setShowAlert(true);
        setAlertVariant("danger");
        setAlertMessage("Email or password is incorrect.");
      }
    } catch (error) {
      console.error(error);
      setShowAlert(true);
      setAlertVariant("danger");
      setAlertMessage("An error occurred. Please try again later.");
    }
  };
  
  return (
    <div className="App">
      {/* Render Navbar only if isLoggedIn is true */}
      {isLoggedIn && <Navbar />}
      <div className="container">
        <div className="row d-flex justify-content-center">
          <div className="col-md-4">
            {showAlert && (
              <Alert variant={alertVariant} onClose={() => setShowAlert(false)} dismissible>
                {alertMessage}
              </Alert>
            )}
            <form id="loginform" onSubmit={loginSubmit}>
              <div className="form-group">
                <label>Email address</label>
                <input
                  type="email"
                  className="form-control"
                  id="EmailInput"
                  name="EmailInput"
                  aria-describedby="emailHelp"
                  placeholder="Enter email"
                  onChange={(event) => setEmail(event.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  className="form-control"
                  id="exampleInputPassword1"
                  placeholder="Password"
                  onChange={(event) => setPassword(event.target.value)}
                />
              </div>
              <button type="submit" className="btn btn-primary">
                Submit
              </button>
              <br></br>
              <div>
                <NavLink className="btn btn-secondary mt-2" to="/create_user">
                  Create User
                </NavLink>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
