import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import ConfirmationModal from './ConfirmationModal';

const Record = (props) => (
  <tr>
    <td>{props.record.First_Name}</td>
    <td>{props.record.Last_Name}</td>
    <td>{props.record.Email}</td>
    <td>{props.record.Region}</td>
    <td>{props.record.Rating}</td>
    <td>{props.record.Fee}</td>
    <td>{props.record.Sale}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.record._id}`}>Edit</Link> |
      <button className="btn btn-link"
        onClick={() => {
          setShowConfirmationModal(true);
        }}
      >
        Delete
      </button>
      <ConfirmationModal
        message="Are you sure you want to delete this agent?"
        showConfirmationModal={showConfirmationModal}
        onConfirm={handleDeleteAgent}
        onCancel={handleCancelDelete}
      />
    </td>
  </tr>
);

export default function RecordList() {
  const [records, setRecords] = useState([]);
  const [showConfirmationModal, setShowConfirmationModal] = useState(false);

  // This method fetches the records from the database.
  useEffect(() => {
    async function getRecords() {
      const response = await fetch(`http://localhost:5000/record/`);

      if (!response.ok) {
        const message = `An error occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const records = await response.json();
      setRecords(records);
    }

    getRecords();

    return;
  }, [records.length]);

  // This method will delete a record
  async function deleteRecord(id) {
    await fetch(`http://localhost:5000/${id}`, {
      method: "DELETE"
    });

    const newRecords = records.filter((el) => el._id !== id);
    setRecords(newRecords);
  }

  // This method will delete an agent
  const handleDeleteAgent = () => {
    deleteRecord(agent._id);
    setShowConfirmationModal(false);
  };

  // This method will cancel the deletion of an agent
  const handleCancelDelete = () => {setShowConfirmationModal(false);
  };
  
  return (
  <div>
  <h3>Record List</h3>
  <table className="table">
  <thead className="thead-light">
  <tr>
  <th>First Name</th>
  <th>Last Name</th>
  <th>Email</th>
  <th>Region</th>
  <th>Rating</th>
  <th>Fee</th>
  <th>Sale</th>
  <th>Action</th>
  </tr>
  </thead>
  <tbody>
  {records.map((record) => (
  <Record
  record={record}
  key={record._id}
  onDelete={() => deleteRecord(record._id)}
  />
  ))}
  </tbody>
  </table>
  </div>
  );
  }
  
  /*
  This is a component that displays a list of records, with the ability to edit or delete a record.
  The component makes a request to a RESTful API to get the records and then displays them in a table.
  Each record is displayed in a row, with columns for the record's First Name, Last Name, Email, Region, Rating, Fee, Sale, and actions.
  The actions column contains two buttons: Edit and Delete.
  Clicking the Edit button takes the user to a page where they can edit the record.
  Clicking the Delete button displays a confirmation modal asking the user if they are sure they want to delete the record.
  If the user confirms, the record is deleted and the table is updated.
  */
