import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { NavLink } from "react-router-dom";

const Transaction = (props) => (
  <tr>
    <td>{props.transaction.company_name}</td>
    <td>{props.transaction.transaction_number}</td>
    <td>{props.transaction.email}</td>
    <td>{props.transaction.sale}</td>
    <td>
      <Link className="btn btn-link" to={`/edit/${props.transaction._id}`}>Edit</Link> |
      <button className="btn btn-link"
        onClick={() => {
          props.deleteTransaction(props.transaction._id);
        }}
      >
        Delete
      </button>
    </td>
  </tr>
);

export default function TransactionList() {
  const [transaction, setTransaction] = useState([]);

  // This method fetches the records from the database.
  useEffect(() => {
    async function getTransaction() {
      const response = await fetch(`http://localhost:5000/transaction/`);

