import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function Create() {
  const [form, setForm] = useState({
    agent_name: "",
    transaction_number: "",
    sale: ""
  });
  const navigate = useNavigate();

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };

    await fetch("http://localhost:5000/transaction/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ 
    agent_name: "",
    transaction_number: "",
    sale: "" });
    navigate("/transaction");
  }

  // This following section will display the form that takes the input from the user.
  return (
    <div>
      <h3>Create New Transaction</h3>
      <form onSubmit={onSubmit}>
      <div className="form-group">
        <label htmlFor="Region">Region</label>
          <select
            className="form-control"
            id="Region"
            value={form.Region}
            onChange={(e) => updateForm({ Region: e.target.value })}
           >
            <option value="">Select Agent</option>
            <option value="North">Go</option>
            <option value="South">South</option>
            <option value="East">East</option>
            <option value="West">West</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="transaction_number">Transaction Number</label>
          <input
            type="text"
            className="form-control"
            id="transaction_number"
            value={form.transaction_number}
            onChange={(e) => updateForm({ transaction_number: e.target.value })}
          />
        </div>

        <div className="form-group">
          <label htmlFor="Sale">Sale</label>
          <input
            type="text"
            className="form-control"
            id="Sale"
            value={form.Sale}
            onChange={(e) => updateForm({ Sale: e.target.value })}
          />
        </div>
        
        <div className="form-group">
          <input
            type="submit"
            value="Create Transaction"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
  );
}
