import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import PropTypes from "prop-types";

const Transaction = ({ transaction, deleteTransaction }) => {
  const { _id, company_name, transaction_number, email, sale } = transaction;

  return (
    <tr>
      <td>{company_name}</td>
      <td>{transaction_number}</td>
      <td>{email}</td>
      <td>{sale}</td>
      <td>
        <Link className="btn btn-link" to={`/edit/${_id}`}>
          Edit
        </Link>{" "}
        |
        <button
          className="btn btn-link text-danger"
          onClick={() => {
            deleteTransaction(_id);
          }}
        >
          Delete
        </button>
      </td>
    </tr>
  );
};

Transaction.propTypes = {
  transaction: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    company_name: PropTypes.string.isRequired,
    transaction_number: PropTypes.number.isRequired,
    email: PropTypes.string.isRequired,
    sale: PropTypes.number.isRequired,
  }),
  deleteTransaction: PropTypes.func.isRequired,
};

const TransactionList = () => {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    async function getTransactions() {
      try {
        const response = await fetch(`http://localhost:5000/transaction-data/`);

        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }

        const transactions = await response.json();
        setTransactions(transactions);
      } catch (error) {
        console.error(error);
        // Display error message using a toast or a modal
      }
    }

    getTransactions();
  }, []);

  async function deleteTransaction(id) {
    try {
      await fetch(`http://localhost:5000/transaction/${id}`, {
        method: "DELETE",
      });

      setTransactions((prevTransactions) =>
        prevTransactions.filter((transaction) => transaction._id !== id)
      );
    } catch (error) {
      console.error(error);
      // Display error message using a toast or a modal
    }
  }

  function transactionList() {
    return transactions.map((transaction) => (
      <Transaction
        transaction={transaction}
        deleteTransaction={deleteTransaction}
        key={transaction._id}
      />
    ));
  }

  return (
    <div>
      <div>
        <NavLink className="btn btn-secondary mt-2" to="/createTransaction">
          Create Transaction
        </NavLink>
      </div>
      <h3>Transaction List</h3>
      <table className="table table-striped" style={{ marginTop: 20 }}>
        <thead>
          <tr>
            <th>Company Name</th>
            <th>Transaction Number</th>
            <th>Email</th>
            <th>Sale</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{transactionList()}</tbody>
      </table>
    </div>
  );
};

export default TransactionList;
